package Loops;

import java.util.Scanner;

public class for_loop_demo {

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int i;
		for(i=345;i>=23;i--)
		{
			
			System.out.print(i+"\t");
			if(i%10==0)
			{
				System.out.println();
			}
		}
	
		
	/*	System.out.print("\"Hiii\" \t iiiii");
		System.out.print("\nHiiiiiiii");
		*/
	}

}
